import '../styling/LandingPage.css';
import Typed from 'typed.js';
import { useEffect } from 'react';
import { useRef } from 'react';
import Image from 'react-bootstrap/Image';


const LandingPage = () => {

    const el = useRef(null);
    const typed = useRef(null);

    useEffect(() => {
        const options = {
            strings: [
            'software engineer.',
            'teacher.',
            'tennis player.', 
            'dog mom.', 
            'woman with grit making a change.'
          ],
          typeSpeed: 100,
          backSpeed: 100,
          loop: true
        };

        // elRef refers to the <span> rendered below
        typed.current = new Typed(el.current, options);

        return () => {
          // Make sure to destroy Typed instance during cleanup
          // to prevent memory leaks
          typed.current.destroy();
        }
      }, [])



    return (
        <div>
            <div class="iam">
                {/* <div class="helloworld">Hello world!</div><button class="button">Translate</button> */}
                <div className="type-container">
                    <h1> I am a <span className="typed" style={{ whiteSpace: 'pre' }} ref={el} /> </h1>
                </div>

                <div>
                  <Image className="gizmo" src="../images/about/meandgizmo.jpeg" />
                </div>

                <a href="https://www.linkedin.com/in/tiffanylast" target="_blank"> LinkedIn </a>
                <a href="https://github.com/tifflastimosa/Portfolio" target="_blank"> GitHub </a>
                <a href="mailto:lastimosa.ti@northeastern.edu"> E-mail </a>
            </div>
        </div>
    )

}

export default LandingPage;
